mapper.select:

select

from t_order
where ORDER_ID = #{orderId,jdbcType=INTEGER}
  and ORDER_PUB_USER_ID = #{orderPubUserId,jdbcType=INTEGER}
  and ORDER_USER_ID = #{orderUserId,jdbcType=INTEGER}
  and ORDER_COMMODITY_ID = #{orderCommodityId,jdbcType=INTEGER}

mapper.delete:

delete from t_order
where ORDER_ID = #{orderId,jdbcType=INTEGER}
  and ORDER_PUB_USER_ID = #{orderPubUserId,jdbcType=INTEGER}
  and ORDER_USER_ID = #{orderUserId,jdbcType=INTEGER}
  and ORDER_COMMODITY_ID = #{orderCommodityId,jdbcType=INTEGER}
